<div class="panel panel-default">	<div class="panel-heading">当前话题</div>	<div class="panel-body">		<?php foreach($problems as $problem) : ?>		<div class="media">			<span class="label label-info pull-right" style="margin-top:5px;">				<?php echo anchor('/problem/show/'.$problem->id, $problem->solutions_count, 'style="color:white;"'); ?>			</span>		  <a class="pull-left" href="<?php echo "/user/show/".$problem->user->id;?>">		    <img class="media-object" src="<?php echo  $problem->user->userInfo->avatar;?>" width="48" height="48" alt="...">		  </a>		  <div class="media-body">		    <h4 class="media-heading">		    	<?php echo anchor('/problem/show/'.$problem->id, $problem->title); ?>		    </h4>		    <?php echo time_ago($problem->created_at) 		    			. ' by ' . $problem->user->username; ?>		    <?php 		    	if(isset($problem->reply_user)) {		    		echo  ' | 最后回复 '.time_ago($problem->replied_at)						.' by '.$problem->reply_user->username; 				} 			?>		  </div>		</div>		<?php endforeach; ?>		<div><?php echo $links;?></div>	</div> <!-- end panel-body --></div> <!-- end panel panel-success -->